// these functions were converted from Home Assistant's python version of it.
// https://github.com/home-assistant/home-assistant/blob/05ecc5a1355c7af11b5d310470a6171528dc7a2b/homeassistant/util/color.py

var color_temperature_to_rgb = function(color_temperature_kelvin) {
    /*
    Return an RGB color from a color temperature in Kelvin.
    This is a rough approximation based on the formula provided by T. Helland
    http://www.tannerhelland.com/4435/convert-temperature-rgb-algorithm-code/
    */
    // range check
    if(color_temperature_kelvin < 1000) {
        color_temperature_kelvin = 1000;
    } else if(color_temperature_kelvin > 40000) {
        color_temperature_kelvin = 40000;
    }

    tmp_internal = color_temperature_kelvin / 100.0;

    red = _get_red(tmp_internal);
    green = _get_green(tmp_internal);
    blue = _get_blue(tmp_internal);

    return [red, green, blue];
};

var _get_red = function(temperature) {
    // Get the red component of the temperature in RGB space.
    if(temperature <= 66){
        return 255;
    }
    tmp_red = 329.698727446 * Math.pow(temperature - 60, -0.1332047592);
    return _bound(tmp_red);
}

var _get_green = function(temperature) {
    // Get the green component of the given color temp in RGB space.
    if(temperature <= 66){
        green = 99.4708025861 * Math.log(temperature) - 161.1195681661;
    } else {
        green = 288.1221695283 * Math.pow(temperature - 60, -0.0755148492);
    }
    return _bound(green);
}

var _get_blue = function(temperature) {
    // Get the blue component of the given color temperature in RGB space.
    if(temperature >= 66){
        return 255;
    } else if(temperature <= 19){
        return 0;
    }
    blue = 138.5177312231 * Math.log(temperature - 10) - 305.0447927307;
    return _bound(blue);
}

var _bound = function(color_component, minimum=0, maximum=255) {
    /*
    Bound the given color component value between the given min and max values.
    The minimum and maximum values will be included in the valid output.
    i.e. Given a color_component of 0 and a minimum of 10, the returned value
    will be 10.
    */
    color_component_out = Math.max(color_component, minimum);
    return Math.min(color_component_out, maximum);
}

msg.circadian.rgb = color_temperature_to_rgb(msg.circadian.kelvin);
msg.circadian.mired = Math.floor(1000000/msg.circadian.kelvin)

return msg;
